package com.example.myapplication.adapter;

import android.content.Context;
import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import android.util.Base64;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ArrayAdapter;
import android.widget.ImageView;
import android.widget.TextView;

import com.example.myapplication.entity.Exercise;
import com.example.myapplication.R;

import java.util.List;

public class ExerciseAdapter extends ArrayAdapter<Exercise> {

    private LayoutInflater inflater;
    private int layout;
    private List<Exercise> exercises;

    public ExerciseAdapter(Context context, int resource, List<Exercise> exercises) {
        super(context, resource, exercises);
        this.exercises = exercises;
        this.layout = resource;
        this.inflater = LayoutInflater.from(context);
    }
    public View getView(int position, View convertView, ViewGroup parent) {

        View view=inflater.inflate(this.layout, parent, false);

        ImageView imageView = (ImageView) view.findViewById(R.id.image);
        TextView nameView = (TextView) view.findViewById(R.id.name);
        TextView musclesView = (TextView) view.findViewById(R.id.muscles);

        Exercise exercise = exercises.get(position);

        //imageView.setImageResource(exercise.getImage());
        //String strBase64 = "";
        String strBase64 = exercise.getImageBase64();
        if (strBase64 != null && strBase64.length()>0){
            byte[] decodedString = Base64.decode(strBase64, Base64.DEFAULT);
            Bitmap decodedByte = BitmapFactory.decodeByteArray(decodedString, 0, decodedString.length);
            imageView.setImageBitmap(decodedByte);
        }

        nameView.setText(exercise.getName());
        musclesView.setText(exercise.getMuscles());

        return view;
    }
}